		/// <summary>
		///     Get Register Content
		/// </summary>
		/// <remarks>Returns the Programming card in the robots register</remarks>
		/// <param name="gameId"></param>
		/// <param name="robotId"></param>
		/// <param name="register">The number of the register</param>
		/// <response code="200">OK</response>
		/// <response code="404">Not Found</response>
		[HttpGet]
		[Route("/v1/games/{game_id}/entities/robots/{robot_id}/registers/{register}")]
		[GameAuth(Role.PLAYER, allowConsumer: true)]
		[ValidateModelState]
		[SwaggerOperation("GetRegisterContent")]
		[SwaggerResponse(200, type: typeof(RobotCommand), description: "OK")]
		public virtual IActionResult GetRegisterContent([FromRoute(Name = "game_id")] [Required] [Range(0, 2048)]
														int gameId, [FromRoute(Name = "robot_id")] [Required] int robotId,
														[FromRoute] [Required] [Range(0, 4)] int register) {
			return new GameRequestPipeline()
				   .Game(gameId)
				   .Robot(robotId)
				   .Compute(code: c => {
					   int card = c.Game.Programming.GetRegister(robotId)[register];
					   if (card == -1)
						   c.SetNotFoundResponse(new ErrorMessage {
							   Error = "Register Empty"
						   });
					   else
						   c.SetResponse(c.Game.Programming[card]);
				   })
				   .ExecuteSecure();
		}

